const PaginationTable = () => {
  const rowStyle = { textAlign: "center" };

  return (
    <Card className="border-0">
      <CardBody>
        <BootstrapTable
          wrapperClasses="table-responsive"
          keyField="name"
          data={tableData}
          columns={tableColumns}
          bootstrap4
          bordered={true}
          rowStyle={rowStyle}
          pagination={paginationFactory({
            sizePerPage: 5,
            sizePerPageList: [5, 10, 25, 50]
          })}
        />
      </CardBody>
    </Card>
  );
};

